import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import "../Bootstrap/Style.css";

function Task({ index, t, onDelete, onTaskChecked, onTaskUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [updatedText, setUpdatedText] = useState(t.name);

  const handleDelete = () => {
    onDelete(index);
    console.log('Suppression de la tâche : "' + t.name + '"');
  };

  const handleCheck = () => {
    onTaskChecked(index);
    console.log('Validation de la tâche : "' + t.name + '"');
  };

  const handleUpdateText = () => {
    onTaskUpdate(updatedText);
    console.log('Modification de la tâche : "' + t.name + '"');
    setIsEditing(false);
  };

  return (
    <div className="task">
      <div style={{ textDecoration: t.completed ? "line-through" : "none" }}>
        {isEditing ? (
          <>
            <input
              type="text"
              value={updatedText}
              onChange={(e) => setUpdatedText(e.target.value)}
            />
            <button onClick={handleUpdateText}>Modifier la tâche</button>
          </>
        ) : (
          <>
            {t.name}
          </>
        )}
      </div>
      <div className="actions">
        <button onClick={() => setIsEditing(!isEditing)}>
          <FontAwesomeIcon icon="pen" />
        </button>
        <button onClick={handleCheck}>
          <FontAwesomeIcon icon="check" />
        </button>
        <button onClick={handleDelete}>
          <FontAwesomeIcon icon="xmark" />
        </button>
      </div>
    </div>
  );
}


export default Task;
