import React from "react";
import TaskProps from "./TaskProps";
import NewTask from "./NewTask";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Button } from 'react-bootstrap';

function Task({ index, t, onDelete, onTaskChecked, onTaskUpdate }) {
  // console.log("Task Props:", t);
  const [isEditing, setIsEditing] = useState(false);
  const [updatedText, setUpdatedText] = useState(t.name);

  const handleDelete = () => {
    onDelete(index);
    console.log('Suppression  de la t창che : "' + t.name +'"');
  };

  const handleCheck = () => {
    onTaskChecked(index);
    console.log('Validation de la t창che : "' + t.name + '"');
  };

  const handleUpdateText = () => {
    onTaskUpdate(updatedText);
    setIsEditing(false);
  };


  return (
    <div>
      <ul>
        <li style={{ textDecoration: t.completed ? 'line-through' : 'none' }}>
          {isEditing ? (
            <>
              <input
                type="text"
                value={updatedText}
                onChange={(e) => setUpdatedText(e.target.value)}
              />
              <button onClick={handleUpdateText}>Update</button>
            </>
          ) : (
            <>
              {t.name}{" "}
              <button onClick={() => setIsEditing(true)}>
                <FontAwesomeIcon icon="pen" />
              </button>
            </>
          )}
          <button onClick={handleDelete}>
            <FontAwesomeIcon icon="xmark" />
          </button>
          {/* <Button type="primary">Bouton</Button> */}
        </li>
      </ul>
    </div>
  );
}

export default Task;
