import React, { useState } from "react";
import TaskProps from "./TaskProps";
import NewTask from "./NewTask";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Button } from "react-bootstrap";
// import "../Bootstrap/Style.css";

function Task({ index, t, onDelete, onTaskChecked, onTaskUpdate }) {
  // console.log("Task Props:", t);
  // État local qui permet de gérer l'édition de text et la mise à jour du texte
  const [isEditing, setIsEditing] = useState(false);
  const [updatedText, setUpdatedText] = useState(t.name);

  const handleDelete = () => {
    onDelete(index);
    console.log('Suppression  de la tâche : "' + t.name + '"');
  };

  const handleCheck = () => {
    onTaskChecked(index);
    console.log('Validation de la tâche : "' + t.name + '"');
  };

  const handleUpdateText = () => {
    // Appelle la fonction onTaskUpdate avec le texte mis à jour
    onTaskUpdate(updatedText);
    // Désactive le mode édition
    setIsEditing(false);
  };

  return (
    <div className="container">
      <ul>
        <li style={{ textDecoration: t.completed ? "line-through" : "none" }} className={t.completed ? 'completed-task' : ''}>
          {isEditing ? (
            <>
              <input
                type="text"
                value={updatedText}
                onChange={(e) => setUpdatedText(e.target.value)}
              />
              <button onClick={handleUpdateText}>Modifier la tâche</button>
            </>
          ) : (
            <>
              {t.name}
              <button onClick={() => setIsEditing(true)}>
                <FontAwesomeIcon icon="pen" />
              </button>
              <button onClick={handleCheck}>
                <FontAwesomeIcon icon="check" />
              </button>
              <button onClick={handleDelete}>
                <FontAwesomeIcon icon="xmark" />
              </button>
            </>
          )}
        </li>
      </ul>
    </div>
  );
}
export default Task;
