import React, { useState } from "react";
import Task from "./Task";
import TaskProps from "./TaskProps";
import NewTask from "./NewTask";

// Un tâbleau de tâches
let tasksList = [
  new TaskProps("Tâche 1"),
  new TaskProps("Tâche 2"),
  new TaskProps("Tâche 3"),
];

function TaskList() {
    const [tasks, setTasks] = useState([]);

    const handleNewTask = (newTask) => {
        setTasks([...tasks, newTask])
    }

  return (
    <div>
      <h1>Liste des tâches</h1>

      {/* J'importe le composant "NewTask" qui me permet d'ajouter une nouvelle tâche */}
      <NewTask onSubmit={handleNewTask}/>

      {/* Grâce à la fonction map, j'affiche la liste de mes tâches du tableau tasksList */}
      {tasks.map((t, index) => (
        <Task key={index} t={t} />
      ))}
    </div>
  );
}

export default TaskList;
