import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import TaskProps from "./TaskProps";
// import "../Bootstrap/Style.css";

function Task({ index, t, onDelete, onTaskChecked, onTaskUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [updatedText, setUpdatedText] = useState(t.name);

  const handleDelete = () => {
    // Afficher une alerte de confirmation avant la suppression
    const confirmDelete = window.confirm(
      `Êtes-vous sûr de vouloir supprimer la tâche : "${t.name}" ?`
    );

    if (confirmDelete) {
      onDelete(index);
      console.log('Suppression de la tâche : "' + t.name + '"');
    }
  };

  const handleCheck = () => {
    onTaskChecked(index);
    console.log('Validation de la tâche : "' + t.name + '"');
  };

  const handleUpdateText = () => {
    onTaskUpdate(updatedText);
    console.log('Modification de la tâche : "' + t.name + '"');
    setIsEditing(false);
  };

  return (
    <tr style={{ borderBottom: '1px solid #ddd' }}>
      <td style={{ textDecoration: t.completed ? "line-through" : "none",backgroundColor: t.completed ? "#dff5c9" : "initial" }}>{index+1}</td>
       <td style={{ textDecoration: t.completed ? "line-through" : "none", backgroundColor: t.completed ? "#dff5c9" : "initial" }}>
        {isEditing ? (
          <>
            <input
              type="text"
              value={updatedText}
              onChange={(e) => setUpdatedText(e.target.value)}
            />
            <button className="edit-btn" onClick={handleUpdateText}>Modifier la tâche</button>
          </>
        ) : (
          <>{t.name}</>
        )}
      </td>
      <td style={{ textDecoration: t.completed ? "line-through" : "none", backgroundColor: t.completed ? "#dff5c9" : "initial" }}>
        {t.creationDate}
      </td>
      <td className="btn-actions" style={{ backgroundColor: t.completed ? "#dff5c9" : "initial"}}>
        {!isEditing && (
          <>
            <button onClick={() => setIsEditing(!isEditing)}>
              <FontAwesomeIcon icon="pen" />
            </button>
            <button className="check-btn" onClick={handleCheck} style={{ backgroundColor: t.completed ? "green" : "var(--accent)"}}>
              <FontAwesomeIcon icon="check" />
            </button>
            <button className="delete-btn" onClick={handleDelete}>
              <FontAwesomeIcon icon="xmark" />
            </button>
          </>
        )}
      </td>
    </tr>
  );
}

export default Task;
