import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Task({ index, t, onDelete, onTaskChecked, onTaskUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [updatedText, setUpdatedText] = useState(t.name);

  useEffect(() => {
    setUpdatedText(t.name);
  }, [t.name]);

  const handleDelete = () => {
    onDelete(index);
    console.log('Suppression de la tâche : "' + t.name + '"');
  };

  const handleCheck = () => {
    onTaskChecked(index);
    console.log('Validation de la tâche : "' + t.name + '"');
  };

  const handleUpdateText = () => {
    onTaskUpdate(index, updatedText);
    console.log('Modification de la tâche : "' + updatedText + '"');
    setIsEditing(false);
  };

  return (
    <tr>
      <td style={{ textDecoration: t.completed ? "line-through" : "none" }}>
        {isEditing ? (
          <>
            <input
              type="text"
              value={updatedText}
              onChange={(e) => setUpdatedText(e.target.value)}
            />
          </>
        ) : (
          <>
            {t.name}
          </>
        )}
      </td>
      <td>
        {isEditing ? (
          <>
            <button onClick={handleUpdateText}>Modifier la tâche</button>
          </>
        ) : (
          <>
            <button onClick={() => setIsEditing(true)}>
              <FontAwesomeIcon icon="pen" />
            </button>
            <button onClick={handleCheck}>
              <FontAwesomeIcon icon="check" />
            </button>
            <button onClick={handleDelete}>
              <FontAwesomeIcon icon="xmark" />
            </button>
          </>
        )}
      </td>
    </tr>
  );
}

export default Task;
